INSERT INTO `g_test_content_module` VALUES ('70', null, '7', '简答题', null, '1.上臂围指沉肩峰与尺骨鹰嘴连接中点水平绕上臂1周 2.上臂围代表上臂骨骼，肌肉，皮下脂肪和皮肤的发育，常用以估评小儿营养状况。一般认为1-7岁小儿上臂围超过13.5cm为营养过剩，12.5～13.5cm为营养中等，小于12.5cm为营养不良', '0', '测量上臂围的意义？', '儿科基础', '小儿生长发育,体重增长规律', '3', '1', '2', '3', '5', '1', '0.00', '');
