INSERT INTO `g_test_content_module` VALUES ('121', null, '1', 'A1型题', null, 'D', '0', '关于小儿胸腺的发育，下列哪项不正确\n\n', '儿科基础', '小儿生长发育,免疫机能发育', '2', '1', '2', '2', '1', '1', '0.04', 'A.生后2月内增长最快               B.6-11岁达30g左右，以后逐步萎缩\n\nC.X线下可见胸腺                   D.是B细胞发育场所\n\nE.是T细胞发育场所');
