INSERT INTO `g_test_content_module` VALUES ('124', null, '1', 'A1型题', null, 'A', '0', '小儿下列预防接种时间，哪项是错误的？\n\n', '儿科基础', '小儿生长发育,免疫机能发育', '2', '1', '2', '1', '1', '1', '0.02', 'A.卡介苗-2个月以上                 B.灰质炎疫苗-2个月\n\nC.百、白、破疫苗3个月              D.麻疹疫苗-8个月\n\nE.流脑、乙脑疫苗-1岁后');
