INSERT INTO `g_test_content_module` VALUES ('162', null, '1', 'A1型题', null, 'E', '0', '婴儿饮食中蛋白质、脂肪、糖类所供给的热量应占总热量的百分比\n\n    蛋白质     脂肪     糖类\n\n', '儿科基础', '小儿营养,营养物质需要量', '2', '1', '2', '3', '1', '1', '-0.06', 'A.   15％      50％     35％\n\nB.   35％      15％     50％\n\nC.   50％      35％     15％\n\nD.   35％      50％     15％\n\nE.   15％      35％     50％');
