INSERT INTO `g_test_content_module` VALUES ('313', null, '1', 'A1型题', null, 'E', '0', '下列有关婴儿腹泻静脉补液的原则中，哪一项是错误的？\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '1', '2', '0.45', 'A.有循环障碍者，先用2:1等张含钠液扩容\nB.输液速度原则是先快后慢\nC.累积损失量应于8～12小时内补足\nD.按1/3～1/2张含钠液补充继续损失\nE.液疗时最初几天热量供给按110卡/公斤');
INSERT INTO `g_test_content_module` VALUES ('314', null, '1', 'A1型题', null, 'B', '0', '轻度脱水，第一天补液总量\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '2', '1', '2', '3', '1', '1', '0.15', 'A.50～90ml/kg/日                   B.90～120ml/kg/日\n\nC.120～150ml/kg/日                 D.150～180ml/kg/日\n\nE.180～250ml/kg/日');
