INSERT INTO `g_test_content_module` VALUES ('316', null, '1', 'A1型题', null, 'C', '0', '高渗性脱水的治疗，应先用\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '1', '1', '0.22', 'A.生理盐水             B.10％葡萄糖        C.0.3％葡萄糖氯化钠溶液\n\nD.林格氏液             E.1.4％碳酸氢钠溶液');
INSERT INTO `g_test_content_module` VALUES ('317', null, '1', 'A1型题', null, 'C', '0', '高氯性低渗脱水最宜选用\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '1', '2', '0.28', 'A.0.9％氯化钠液        B.1/6M乳酸钠液          C.1/6M碳酸氯钠液\n\nD.葡萄糖液             E.1:1液');
INSERT INTO `g_test_content_module` VALUES ('318', null, '1', 'A1型题', null, 'E', '0', '关于补钾的要求哪项不合适\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '1', '1', '0.33', 'A.一般每日补钾总量按2～4mmol/kg      B.静脉补给的浓度不超过0.3％\n\nC.一日补钾总量不少于6-8小时给入      D.不宜过份强调见尿补钾\n\nE.吐泻好转应及时停止补钾');
