INSERT INTO `g_test_content_module` VALUES ('377', null, '7', '简答题', null, '评分应在刚出生后1分钟内评定，不正常者5分钟时必须再评一次，因5分钟评\n\n分对预后(特别对智力的影响)关系更为密切，仍不正常者可继续每隔10～15\n\n分钟多次重复评分', '0', 'Apgar评分应在什么时候进行，各有什么意义？', '新生儿疾病', '新生儿窒息,临床表现', '1', '1', '2', '2', '3', '2', '0.00', '');
