INSERT INTO `g_test_content_module` VALUES ('385', null, '6', '论述题', null, '纠酸时必须先行辅助呼吸使PaCO2接近正常，然后用碳酸氢钠纠酸，否则碳酸\n\n氢钠将增加血CO2含量', '0', '抢救新生儿窒息纠正酸中毒时应注意的事项是什么？为什么？', '新生儿疾病', '新生儿窒息,治疗', '3', '1', '2', '1', '2', '2', '0.00', '');
