INSERT INTO `g_test_content_module` VALUES ('388', null, '7', '简答题', null, '因肺表面活性物质由肺Ⅱ型细胞产生，在胎儿20-24周时肺内开始出现，但量\n\n不多，到35周以后迅速上升，如分娩过早则此物质量不足，故本症多发生在早\n\n产儿', '0', '为什么新生儿肺透明膜病多见于早产儿？', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '1', '1', '2', '1', '3', '2', '0.00', '');
