INSERT INTO `g_test_content_module` VALUES ('393', null, '7', '简答题', null, '两侧肺野普遍性透明度减低，内有均匀的细小颗粒颗，以后渐融合成片，支气\n\n管则有充气征', '0', '简述新生儿肺透明膜病的X线征', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '1', '1', '2', '3', '2', '2', '0.00', '');
