INSERT INTO `g_test_content_module` VALUES ('395', null, '1', 'A1型题', null, 'D', '0', '新生儿胎龄35周，生后4小时出现气促，紫绀，进行性加重，两肺呼吸音低，吸气时可听到细湿罗音，最可能的诊断是：\n\n', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '3', '1', '2', '2', '2', '1', '0.09', 'A.新生儿窒息                  B.新生儿羊水吸入综合征\n\nC.新生儿肺炎                  D.新生儿肺透明膜病\n\nE.新生儿颅内出血');
