INSERT INTO `g_test_content_module` VALUES ('399', null, '2', 'X型题', null, 'A   C', '0', '湿肺与肺透明膜病有鉴别意义的诊断是\n\n', '新生儿疾病', '新生儿肺透明膜病,诊断及鉴别诊断', '3', '1', '2', '3', '1', '2', '0.00', 'A.多发生在足月儿\n\nB.症状多发生在出生后6小时之内\n\nC.肺部X线表现多样性，如肺泡积液征，间质积液征，肺气肿征等。\n\nD.双肺可闻及湿性罗音\n\nE.胃液振荡试验阴性');
INSERT INTO `g_test_content_module` VALUES ('400', null, '1', 'A1型题', null, 'D', '0', '对于早产极低体重儿预防肺透明膜病的有效措施是\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '3', '1', '2', '3', '1', '1', '-0.01', 'A.抗菌素                   B.正压给氧\n\nC.肾上腺皮质激素           D.气管内滴入肺表面活性物质\n\nE.纠正酸中毒');
