INSERT INTO `g_test_content_module` VALUES ('402', null, '1', 'A1型题', null, 'C', '0', '给一位有发绀和气促的新生儿输100％氧气后，发绀基本消除，此新生儿最可能患\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '1', '1', '2', '1', '1', '1', '0.10', 'A.紫绀型先天性心脏病\nB.新生儿颅内出血\nC.新生儿肺炎\nD.新生儿败血症\nE.以上都不是');
