INSERT INTO `g_test_content_module` VALUES ('489', null, '6', '论述题', null, '应严格执行科学接生法，在紧急情况下无消毒接生用具，可将剪刀用火烧红\n\n冷却后断脐，对脐带处理不当的婴儿，立即用3％过氧化氢或1:4000高锰酸钾\n\n冲洗后涂以碘酒，同时肌注破伤风抗毒素3000单位', '0', '怎样预防新生儿破伤风？', '新生儿疾病', '新生儿破伤风,预防及治疗', '3', '1', '2', '3', '2', '2', '0.00', '');
