INSERT INTO `g_test_content_module` VALUES ('493', null, '2', 'X型题', null, 'E   A   B   C   D', '0', '对于破伤风患儿下述的哪些护理治疗是正确的\n\n', '新生儿疾病', '新生儿破伤风,预防及治疗', '3', '1', '2', '3', '1', '3', '0.00', 'A.保持环境安静，操作要轻\n\nB.避免强光直接照射 \n\nC.发病初应停止哺喂，以静脉点滴供给液体及补充热能\n\nD.患儿出现喉痉挛，窒息，咳嗽及吞咽反射消失，而气管内分泌物较多时，可作气管切开术\n\nE.定时拍背、吸痰');
INSERT INTO `g_test_content_module` VALUES ('494', null, '7', '简答题', null, '1.止痉：可用安定，鲁米那，氯丙嗪等\n\n2.抗感染：青霉素\n\n3.注射破伤风抗毒素\n\n4.保持安静，清除呼吸道分泌物，保持气道通畅，需要时吸氧\n\n5.脐部处理\n\n6.静滴维持水电解质平衡及热量需要', '0', '患儿4天，男，因拒奶，四肢阵发性强直性痉挛1天急诊入院。查体时阵阵痉挛\n\n口唇紫绀，呼吸急促，心率160次/分，肺呼吸音粗，脐部有粘性分泌物，脐周皮\n\n肤充血，腹稍隆浓，肝脾均在肋下1.5cm，请提出治疗意见。(原则)', '新生儿疾病', '新生儿破伤风,预防及治疗', '3', '1', '2', '1', '5', '3', '0.00', '');
