INSERT INTO `g_test_content_module` VALUES ('622', null, '1', 'A1型题', null, 'A', '0', '佝偻病激期与初期鉴别点中，下列哪项不妥\n', '营养性疾病', '维生素D缺乏性佝偻病,诊断和鉴别诊断', '2', '1', '2', '3', '2', '1', '0.25', 'A.血钙明显下降                  B.血磷明显下降\nC.硷性磷酸酶显著增多            D.钙磷乘积显著下降\nE.X线检查明显异常');
INSERT INTO `g_test_content_module` VALUES ('623', null, '6', '论述题', null, '诊断\n\n1.大部分患儿有维生素D缺乏史，轻度佝偻病的症状与体征\n\n2.本病的主要体征有，惊厥，手足搐搦和喉痉挛，惊厥常为婴儿病例的重要表现，一般发作不频，历时较短，但少数因多次长时间抽搐，抽搐过后精神，食欲良好，有的婴儿来为局部，特别是面部肌肉抽搐，手足搐搦常见于幼儿，较大幼儿可诉手足发麻或不适。喉痉挛少见，主要见于婴儿，表现为突然发作的吸气困难，重者可发生窒息。\n\n3.体征主要有面神经征，腓神经征和人工手痉挛症\n\n4.实验室检查\n\n⑴尿钙定性迅速简便，阴性表明血钙减低\n\n⑵血清钙低于7.5mg/dL(1', '0', '怎样诊断婴儿手足搐搦症？处理要点是什么？', '营养性疾病', '维生素D缺乏性佝偻病,诊断和鉴别诊断', '3', '1', '2', '3', '20', '3', '0.00', '');
INSERT INTO `g_test_content_module` VALUES ('624', null, '7', '简答题', null, '1.早期令患儿俯卧，抬头，每日2-3次\n\n2.自动或被动进行扩胸运动，并可轻轻地按摩，以防畸形发展并随年龄增长，经常扩胸或俯卧可使鸡胸减轻', '0', '佝偻病出现鸡胸怎么办？', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '3', '1', '2', '3', '5', '2', '0.00', '');
INSERT INTO `g_test_content_module` VALUES ('625', null, '7', '简答题', null, '1.足月儿生后2-3周，双胎儿生后1-2周，开始服用维生素D剂\n\n2.可用浓缩鱼肝油滴剂从小量始如1-2滴/日渐增至5-6滴/日或其他维生素D剂400-800In/日\n\n3.如有腹泻或其他疾病影响口服维生素D者可用维生素D制剂30万V肌注预防在生后第一年用1-2次，治疗视情况而定，间隔时间应在1月   \n\n4.用维生素D剂时同时适量加服钙剂\n\n5.春夏日能晒太阳时应停用或少用维生素D剂', '0', '如何合理使用维生素D？', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '1', '1', '2', '2', '8', '3', '0.00', '');
