INSERT INTO `g_test_content_module` VALUES ('717', null, '6', '论述题', null, '有过敏体质者接触特异性抗原后产生反应性抗体IgE附于支气管粘膜下肥大\n\n细胞表面使细胞致敏，此IgE再次接触抗原时激活磷酸酶A，使肥大细胞释放\n\n生物介质如组织胺，慢反应物质，缓激肽，嗜酸性趋化因子等。引起支气管平\n\n滑肌收缩，毛细血管通透性增加，分泌物增加→哮喘发作', '0', '简述小儿哮喘发病机理', '呼吸系疾病', '支气管炎,临床表现', '1', '2', '2', '3', '5', '2', '0.00', '');
INSERT INTO `g_test_content_module` VALUES ('718', null, '6', '论述题', null, '1.3岁以下小儿多见，常有湿疹等过敏性疾病史\n\n2.有类似哮喘的症状，呼气性呼吸困难，可闻哮鸣音\n\n3.有白细胞升高，低热等感染症状\n\n4.有反复倾向，一般随年龄增长而发作减少痊愈。少数可在成人期形成支气\n\n管哮喘', '0', '试述婴幼儿哮喘性支气管炎的临床特点', '呼吸系疾病', '支气管炎,临床表现', '3', '1', '2', '3', '5', '2', '0.00', '');
