INSERT INTO `g_test_content_module` VALUES ('727', null, '6', '论述题', null, '', '0', '试述小儿支气管哮喘用平喘药物的止喘作用机理(可列图表示)\n\n ┌ 支气管哮喘是变态反应性疾病，它的发病与体内环磷酸腺苷(CAM)量的\n\n        减少有关，儿茶酚胺和茶硷类药物通过下列机理起止喘作用  \n\n │ 三磷酸腺苷---→ 环磷酸腺苷---→ 单磷酸腺苷(5-Amp)\n\n │  (ATP)    ↑     (CAmp)     ↑ \n\n │         腺苷酸     ｜      磷酸\n\n │         环化酶     ↓      二酯酶\n\n │           ↑', '呼吸系疾病', '支气管炎,治疗', '3', '1', '2', '3', '10', '3', '0.00', '');
