INSERT INTO `g_test_content_module` VALUES ('767', null, '6', '论述题', null, '1.呼吸困难突然加重，烦燥不安，面色苍白或发绀 2.心率增快，婴儿&gt;160/min，新生儿&gt;180/min 3.心音低钝或心脏扩大，心律紊乱 4.肝脏短期内上下经增大1.5cm以上 5 呼吸困难和肺部罗音短期内加重，增多', '0', '试述小儿肺炎合并心力衰竭的诊断要点', '呼吸系疾病', '支气管肺炎,诊断与鉴别诊断', '3', '2', '2', '3', '10', '1', '0.03', '');
INSERT INTO `g_test_content_module` VALUES ('768', null, '6', '论述题', null, '严重喘憋，一减少炎性渗出。解除支气管痉挛\n\n并发脑水肿，中毒性脑病一降颅内压作用\n\n并感染性休克一改善微循环作用\n\n并呼吸衰竭一减少炎性渗出，减轻脑水肿改善呼吸功能', '0', '试述支气管肺炎患儿应用肾上腺皮质激素的适应征和简要机理', '呼吸系疾病', '支气管肺炎,预防及治疗', '3', '2', '2', '3', '5', '2', '0.00', '');
