INSERT INTO `g_test_content_module` VALUES ('853', null, '2', 'X型题', null, 'A B D', '0', '婴儿腹泻中度脱水的主要表现为\n', '消化道疾病', '婴儿腹泻,临床表现', '2', '1', '2', '3', '1', '3', '0.38', 'A.眼窝及前囟凹陷        B.皮肤弹性较差         C.尿量稍减少\nD.烦燥不安              E.四肢厥冷，无尿');
INSERT INTO `g_test_content_module` VALUES ('854', null, '2', 'X型题', null, 'A B C D', '0', '低渗性脱水的临床表现主要是\n', '消化道疾病', '婴儿腹泻,临床表现', '2', '1', '2', '3', '1', '2', '0.00', 'A.容易出现休克现象?                       B.口渴不明显\nC.可因发生脑细胞水肿而易出现嗜睡，昏迷     D.血清钠应&lt;130mmol/L\nE.可发生脑血栓');
