INSERT INTO `g_test_content_module` VALUES ('870', null, '6', '论述题', null, '该病毒是引起婴幼儿腹泻的主要原因。病毒由病人粪便排出，经粪口途径传播，80％以上患儿大便中可检出轮状病毒，多见于6个月～2岁的婴幼儿，在我国轮状病毒是婴儿秋季腹泻的主要病原体起病较急，一般无明显中毒症状，病程一般3～8天。', '0', '人类轮状病毒肠炎', '消化道疾病', '婴儿腹泻,临床表现', '2', '2', '2', '1', '2', '3', '0.00', '');
