INSERT INTO `g_test_content_module` VALUES ('888', null, '1', 'A1型题', null, 'D', '0', '迁延性腹泻病的治疗以下应强调哪点是不妥的？\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '1', '2', '2', '1', '1', '0.28', 'A.针对病因治疗                     B.慎用抗生素\n\nC.有条件时，胃肠道外全营养         D.适当采用禁食疗法\n\nE.加强支持疗法');
INSERT INTO `g_test_content_module` VALUES ('889', null, '1', 'A1型题', null, 'D', '0', '腹泻病患儿有重度脱水，血清钠125mmol/L，静脉补液用以下哪项？\n', '消化道疾病', '婴儿腹泻,治疗', '2', '1', '2', '3', '1', '1', '0.13', 'A.等张含钠液150ml/kg               B.1/3张含钠液120ml/kg\nC.1/2张含钠液120ml/kg              D.2/3张含钠液150ml/kg\nE.高张含钠液150ml/kg');
INSERT INTO `g_test_content_module` VALUES ('890', null, '1', 'A1型题', null, 'C', '0', '腹泻病患儿经补液4小时后的处理应是\n\n', '消化道疾病', '婴儿腹泻,治疗', '3', '1', '2', '3', '1', '1', '0.12', 'A.母亲带患儿离开医院                   B.口服液，恢复正常饮食\n\nC.重新估计患儿的脱水状况选择适当方案   D.帮助母亲给患儿服用ORS液\n\nE.额外补给100-200ml的白开水');
INSERT INTO `g_test_content_module` VALUES ('891', null, '1', 'A1型题', null, 'B', '0', '治疗重度脱水患儿静脉轮液第一阶段宜用\n\n', '消化道疾病', '婴儿腹泻,治疗', '3', '2', '2', '3', '1', '2', '0.37', 'A.4％碳酸氢钠         B.2:1液             C.1.87％乳酸钠\n\nD.3:2:1溶液           E.1.4％碳酸氢钠');
INSERT INTO `g_test_content_module` VALUES ('892', null, '1', 'A1型题', null, 'D', '0', '迁延性腹泻的治疗中，下列哪项是正确的\n\n', '消化道疾病', '婴儿腹泻,治疗', '1', '2', '2', '3', '1', '1', '-0.01', 'A.应长期使用抗生素进行病原治疗\n\nB.禁食时间要长，有利于消化功能的恢复\n\nC.应长期应用脱脂奶\n\nD.寻找并解除引起病程迁延原因，积极治疗并发症\n\nE.可试用抗病毒药物');
INSERT INTO `g_test_content_module` VALUES ('893', null, '1', 'A1型题', null, 'E', '0', '下列哪种液体是等张的\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '3', '1', '1', '0.22', 'A.4:3:2            B.3:2:1              C.5％碳酸氢钠\n\nD.11.2％乳酸钠     E.1.4％碳酸氢钠');
INSERT INTO `g_test_content_module` VALUES ('894', null, '1', 'A1型题', null, 'B', '0', '3:2:1溶液的组成成分是\n\n        10％糖     生理盐水  1.4％NaHco3\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '3', '1', '3', '0.25', 'A.        3份        1份         2份 \n\nB.        3份        2份         1份\n\nC.        2份        1份         3份\n\nD.        1份        2份         3份\n\nE.        2份        3份         1份');
INSERT INTO `g_test_content_module` VALUES ('895', null, '1', 'A1型题', null, 'D', '0', '在以下几种溶液中哪种溶液不是等张的\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '3', '1', '1', '0.01', 'A.2:1等张含钠液(2份生理盐水，1份1.87％乳酸钠)\n\nB.1.4％碳酸氢钠\n\nC.0.9％氯化钠\n\nD.2:3:1溶液\n\nE.1/6M乳酸钠溶液');
INSERT INTO `g_test_content_module` VALUES ('896', null, '2', 'X型题', null, 'A B C E', '0', '腹泻病的原则中包括\n', '消化道疾病', '婴儿腹泻,治疗', '3', '1', '2', '3', '1', '3', '0.38', 'A.预防脱水            B.治疗脱水            C.早期进食\nD.积极用药            E.加强护理');
INSERT INTO `g_test_content_module` VALUES ('897', null, '2', 'X型题', null, 'A B D E', '0', 'ORS口服液治疗腹泻病下列哪项是正确的\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '1', '1', '3', '0.22', 'A.适用于轻度脱水       B.吐泻明显或腹胀者不适用\nC.为1/2张含钠液        D.2％左右的葡萄糖溶液可增加小肠对水盐的吸收\nE.不适用于新生儿');
INSERT INTO `g_test_content_module` VALUES ('898', null, '2', 'X型题', null, 'A   B   C   E', '0', '重症腹泻病静脉补钾盐时应注意\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '3', '1', '2', '0.00', 'A.治疗前6h内排过尿或输液后有尿\n\nB.点滴浓度不宜超过0.15～0.3％\n\nC.静脉滴注时间不宜少于8h(指全天总量)\n\nD.总量每天每公斤体重氯化钾0.6克\n\nE.一般持续4～6天');
INSERT INTO `g_test_content_module` VALUES ('899', null, '5', '名词解释题', null, '组成成分为氯化钠3.5g，碳酸氢钠2.5g，氯化钾1.5g，无水葡萄糖20.0g，加水至1000ml.用于轻，中度腹泻病患儿，简便易行，疗效较好。', '0', '口服补液盐((ORS)', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '1', '2', '2', '0.00', '');
INSERT INTO `g_test_content_module` VALUES ('900', null, '6', '论述题', null, '对缺钾患儿必须及时补充钾盐，但若静脉滴注射时速展太快，剂量太大，深度浓度较高，反而会导致高血钾造成危险因此补充钾盐不仅要注意肾脏的排尿功能。而且补充速度不可太快，剂量不可太大，静脉滴入速度一般以每小时不超过20毫当量为宜，滴入浓度不超过0.15～0.3％，口服较为安全，能口服者尽量口服，氯化钾口服对胃肠道刺激较强，因此多采用10％水溶液，饭后服可减少刺激性。', '0', '静脉滴注补充钾盐应注意什么？', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '3', '5', '2', '0.00', '');
INSERT INTO `g_test_content_module` VALUES ('901', null, '1', 'A1型题', null, 'B', '0', '腹泻病有重度低钾症状者，应用下列溶液纠正：\n\n', '消化道疾病', '婴儿腹泻,治疗', '3', '2', '2', '3', '1', '1', '0.28', 'A.高张含钠液       B.0.15～0.3％氯化钾液      C.10％液氯化钾液\n\nD.2:1液            E.4:3:2液');
INSERT INTO `g_test_content_module` VALUES ('902', null, '2', 'X型题', null, 'A   B   D', '0', '迁延性腹泻病静脉补钾原则是\n\n', '消化道疾病', '婴儿腹泻,治疗', '1', '1', '2', '3', '1', '3', '0.00', 'A.适用于中，重度缺钾时        B.滴注时间不应少于6-8小时\n\nC.有脱水者均需静滴            D.氯化钾静脉点滴浓度不宜超过0.3％\n\nE.一般剂量为每天6-8mmol/kg');
INSERT INTO `g_test_content_module` VALUES ('903', null, '2', 'X型题', null, 'A D E', '0', '对婴儿腹泻病患儿的饮食疗法，以下哪些是正确的\n', '消化道疾病', '婴儿腹泻,治疗', '2', '1', '2', '3', '1', '3', '0.18', 'A.暂时禁食，但不宜过久\nB.严重呕吐者禁食后，饮食由少到多，由稀到浓，逐渐过渡到正常饮食\nC.一律禁食，给予静脉补液\nD.继续饮食，合理用药\nE.病毒性肠炎暂停乳类喂养，改为豆制代乳品');
INSERT INTO `g_test_content_module` VALUES ('904', null, '1', 'A1型题', null, 'A', '0', '4:3:2溶液的组成成是：\n\n   10％葡萄糖   生理盐水    1.4％NaHco3\n\n', '消化道疾病', '婴儿腹泻,治疗', '2', '1', '2', '3', '1', '1', '0.15', 'A.     3份        4份           2份\n\nB.     4份        2份           3份\n\nC.     2份        4份           3份\n\nD.     4份        3份           2份\n\nE.     2份        3份           4份');
INSERT INTO `g_test_content_module` VALUES ('905', null, '2', 'X型题', null, 'A B C D E', '0', '应如何预防婴儿腹泻病\n', '消化道疾病', '婴儿腹泻,治疗', '2', '2', '2', '1', '1', '2', '0.00', 'A.合理喂养，鼓励母乳喂养，避免夏季断奶添加辅食，要采取逐渐过渡的方式\nB.注意食物，要采取逐渐过渡的方式\nC.感染性腹泻流行期间，幼托机构及医院更应注意消毒隔离\nD.避免长期滥用，广谱抗生素\nE.注意气候变化时的护理，避免过热或受凉');
