INSERT INTO `g_test_content_module` VALUES ('962', null, '2', 'X型题', null, 'A B C D E', '0', '病毒性心肌炎并发心衰应用尖洋地黄制剂应注意：\n', '循环系统疾病', '病毒性心肌炎,治疗', '1', '1', '2', '1', '2', '3', '0.19', 'A.选用作用快，排泄快制剂\nB.饱和量较常用量减少\nC.洋地黄化时间延长\nD.心衰控制后，维持量继用1个月以上\nE.与利尿合用，可减少洋地黄用量');
