INSERT INTO `g_test_content_module` VALUES ('998', null, '1', 'A1型题', null, 'C', '0', '常见的左向右分流型和无分流型先天性心脏病大都能实行手术根治，一般最\n\n适合手术的年龄是\n\n', '循环系统疾病', '先天性心脏病的诊断与治疗,预后', '1', '1', '2', '1', '1', '1', '0.13', 'A.新生儿期(出生至28天)        B.婴幼儿期(1月-3岁)\n\nC.学龄前期(3-7岁)             D.学龄前(7-12岁)\n\nE.青春期(12-18岁)');
