INSERT INTO `g_test_content_module` VALUES ('1117', null, '2', 'X型题', null, 'A   C   D', '0', '小儿原发综合征诊断的依据包括\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '2', '2', '2', '3', '1', '1', '0.00', 'A.病史可有长期低热，食欲不振，体重减轻等\n\nB.无明确的结核接触史\n\nC.未接种过卡介苗，结核菌素试验阳性\n\nD.胸部X线摄片，肺门阴影增大\n\nE.白细胞计数升高');
