INSERT INTO `g_test_content_module` VALUES ('1142', null, '1', 'A1型题', null, 'B', '0', '接种卡介苗，所使用的菌苗是\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '2', '2', '2', '1', '1', '1', '0.00', 'A.不灭毒活的牛型结核杆菌      B.灭毒的活的牛型结核杆菌\n\nC.灭毒的鸟型结核杆菌          D.灭毒的活的人型结核杆菌\n\nE.灭毒的鼠型结核杆菌');
