INSERT INTO `g_test_content_module` VALUES ('1171', null, '7', '病例分析题', null, '诊断：原发性肺结核\n药物治疗方案：宜同时选用两种杀菌药物，即异菸肼及链霉素或利福平，并配\n合一种抑菌药常用者为乙胺丁醇。异菸肼，疗程18个月，利福平及乙胺丁醇疗\n程为6～12个月疵顾亓瞥?周', '0', '7岁女孩，低热半月，纳差、消瘦、轻咳、盗汗、乏力、体温38℃，发育好，营\n养良，右背下部听诊呼吸音稍减低，下肢有少数结节性红斑，眼部有疱疹性结\n膜炎。示接种过卡介苗，结核菌素试验1:2000强阳性，胸部X线摄片见肺门淋\n巴结肿大。请提出诊断及药物治疗方案', '小儿结核病', '小儿原发性肺结核,治疗', '3', '2', '2', '2', '5', '2', '0.00', '');
