INSERT INTO `g_test_content_module` VALUES ('1174', null, '7', '简答题', null, '1.3岁以下婴幼儿未接种过卡介苗，但新近结素试验呈阳性反应\n\n2.与开放性结核病人密切接触者\n\n3.未接种过卡介苗的小儿，结素试验由阴转阳\n\n4.结素试验呈强阳性反应者\n\n5.有结核病史或肺内有陈旧的结核病灶，因其他疾病需长期服用肾上腺皮质\n\n激素或其他免疫抑制剂者。', '0', '试述结核病预防性化疗的对象', '小儿结核病', '小儿原发性肺结核,治疗', '1', '1', '2', '3', '3', '2', '0.12', '');
