INSERT INTO `g_test_content_module` VALUES ('1217', null, '7', '简答题', null, '1.脑脊液压力增高\n\n2.外观混浊甚至脓样\n\n3.白细胞多在1000×106/L以上，以中性粒细胞为主\n\n4.糖含量降低，', '0', '试述细菌性脑膜炎的脑脊液典型改变', '神经系统疾病', '细菌性脑膜炎,临床表现和辅助诊断', '1', '1', '2', '1', '3', '2', '0.00', '');
