INSERT INTO `g_test_content_module` VALUES ('1374', null, '6', '论述题', null, '1.病因未明确之前仅用于抢救患儿，一般不用\n\n2.病毒感染早期不用，否则可防碍干扰素的释放\n\n3.细菌感染所致高热用激素同时应予足量有效抗生素，以防感染扩散 \n\n4.急性高热伴中毒症状严重，用激素一般不用超过3天\n\n5.对已诊断或拟诊为需用激素治疗的发热性疾病如结缔组织病，血液病等可\n\n视病情而定', '0', '试述高热患儿应用激素的注意事项', '小儿常见急症', '发热,治疗', '3', '1', '2', '1', '8', '3', '0.00', '');
