INSERT INTO `g_test_content_module` VALUES ('1515', null, '1', 'A1型题', null, 'D', '0', '婴儿腹泻，重度脱水，重度酸中毒，静脉补液宜先给\n\n', '消化道疾病', '婴儿腹泻,治疗', '3', '2', '2', '3', '1', '1', '0.11', 'A.1/2张含钠溶液                  B.2/3张含钠溶液\n\nC.5%碳酸氢钠溶液20ml/kg          D.1.4%碳酸氢钠溶液20ml/kg\n\nE.1.87%乳酸钠溶液20ml/kg');
