INSERT INTO `g_test_content_module_bak` VALUES ('66', null, '2', null, 'A B C D', '0', '小儿体重增长，以下哪项正确：\n', '儿科基础', '小儿生长发育,体重增长规律', '2', '1', '2', '3', '1', '2', '0.20', 'A.6个月内平均每月增长700g          B.2岁以后平均每年增长2kg\nC.7-12个月平均每月增长400g         D.出生体重3kg\nE.7～12月平均每月增长500g');
