INSERT INTO `g_test_content_module_bak` VALUES ('256', null, '2', null, 'A   B   C   D   E', '0', '下列哪些药物可通过母乳作用小儿？\n\n', '儿科基础', '小儿药物治疗特点,药物选用', '2', '1', '2', '1', '1', '3', '0.00', 'A.吗啡               B.溴剂            C.阿托品\n\nD.水扬酸盐           E.杜冷丁');
INSERT INTO `g_test_content_module_bak` VALUES ('257', null, '2', null, 'A C D E', '0', '新生儿应用下列哪些药物可致高胆红素血症，甚至引起核黄疸\n', '儿科基础', '小儿药物治疗特点,药物选用', '1', '1', '2', '1', '2', '3', '0.29', 'A.VitK3          B.青霉素        C.磺胺类\nD.新生霉素       E.菌必治');
INSERT INTO `g_test_content_module_bak` VALUES ('258', null, '2', null, 'C   D', '0', '儿童便秘时，一般不应用：\n\n', '儿科基础', '小儿药物治疗特点,药物选用', '3', '1', '2', '1', '1', '1', '0.00', 'A.甘油栓            B.肥皂           C.石腊油\n\nD.蓖麻油            E.生理盐水灌肠');
INSERT INTO `g_test_content_module_bak` VALUES ('259', null, '2', null, 'A   B   E', '0', '氯霉素引起灰婴综合征的原因是：\n\n', '儿科基础', '小儿药物治疗特点,药物选用', '3', '1', '2', '1', '1', '2', '0.00', 'A.早产儿肝脏缺乏葡萄糖醛酸转换酶\n\nB.氯霉素不能在体内与葡萄糖醛酸结合而解毒\n\nC.早产儿肝脏缺乏乳酸脱氯酶\n\nD.氯霉素不能在体内与乳酸脱氢酶结合而解毒\n\nE.新生儿肝脏酶系统未完善');
