INSERT INTO `g_test_content_module_bak` VALUES ('306', null, '1', null, 'B', '0', '3:2:1液是\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '1', '1', '2', '3', '1', '1', '0.01', 'A.4份0.9％氯化钠：10％葡萄糖：1.4％碳酸氢钠2份\nB.2份10％葡萄糖：0.9％氯化钠3份：1.4％碳酸氢钠1份\nC.1份生理盐水+4份5％葡萄糖+0.15％氯化钾\nD.2份0.9％氯化钠：1.4％碳酸氢钠1份\nE.2份0.9％氯化钠：10％葡萄糖1份：0.3％-氯化钾');
