INSERT INTO `g_test_content_module_bak` VALUES ('320', null, '1', null, 'D', '0', '关于纠正代谢性酸中毒，哪项是不正确的？\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '1', '1', '0.07', 'A.轻度代谢性酸毒补液后可不必另给硷性药\n\nB.重度代谢性酸中毒，按提高CO2CP10～15vol％给予硷性药 \n\nC.伴有脱水的代谢性酸中毒给予等渗硷性药\n\nD.按体液总量计算公式要给的硷性药应一次给予\n\nE.积极除去病因');
INSERT INTO `g_test_content_module_bak` VALUES ('321', null, '1', null, 'D', '0', '呼吸性酸中毒的治疗首选\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '1', '1', '1', '0.34', 'A.补充硷性液体纠正酸中毒    B.吸氧           C.应用激素减轻症状\n\nD.改善通气功能              E.人工呼吸');
INSERT INTO `g_test_content_module_bak` VALUES ('322', null, '1', null, 'D', '0', '纠正代谢性硷中毒不用\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '3', '1', '2', '3', '2', '2', '0.39', 'A.氯化铵               B.盐酸精氨酸            C.安体舒通\nD.谷氨酸钠             E.氯化钾');
INSERT INTO `g_test_content_module_bak` VALUES ('323', null, '2', null, 'A   B   E', '0', '下列哪些为1/2张液\n\n', '儿科基础', '小儿体液平衡及液体疗法,小儿体液体疗法', '2', '1', '2', '3', '1', '1', '0.00', 'A.3:2:1液         B.1:1液         C.4:3:2液\n\nD.2:1液           E.0.7%碳酸氢钠液');
