INSERT INTO `g_test_content_module_bak` VALUES ('380', null, '4', null, '吸入综合症', '0', '新生儿窒息最常见的呼吸系统并发症是___________________。', '新生儿疾病', '新生儿窒息,并发症和后遗症', '1', '1', '2', '2', '1', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('381', null, '4', null, '脑性瘫痪     智能低下    耳聋    视力减退    癫痫', '0', '严重新生儿窒息常遗留的后遗症有__________________，_________________，__________________，________________，_________________。', '新生儿疾病', '新生儿窒息,并发症和后遗症', '3', '1', '2', '1', '3', '2', '0.00', '');
