INSERT INTO `g_test_content_module_bak` VALUES ('383', null, '4', null, '160     100    不规则', '0', '胎儿窒息时，胎心率可增至___________次/分以上。或减慢至___________次/分以下，有时心律____________.', '新生儿疾病', '新生儿窒息,临床表现', '2', '1', '2', '1', '1', '1', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('384', null, '1', null, 'C', '0', '新生儿重度窒息Apgar评分\n', '新生儿疾病', '新生儿窒息,临床表现', '2', '1', '2', '1', '1', '3', '0.13', 'A.0-2分    B.3-5分     C.4-7分     D.8-10分     E.以上都不是');
INSERT INTO `g_test_content_module_bak` VALUES ('385', null, '6', null, '纠酸时必须先行辅助呼吸使PaCO2接近正常，然后用碳酸氢钠纠酸，否则碳酸\n\n氢钠将增加血CO2含量', '0', '抢救新生儿窒息纠正酸中毒时应注意的事项是什么？为什么？', '新生儿疾病', '新生儿窒息,治疗', '3', '1', '2', '1', '2', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('386', null, '1', null, 'D', '0', '因母亲使用吗啡而致的新生儿窒息可使用\n\n', '新生儿疾病', '新生儿窒息,治疗', '3', '1', '2', '1', '1', '1', '0.00', 'A.地塞米松   B.氨茶硷    C.消炎痛    D.纳洛酮    E.洛贝林');
INSERT INTO `g_test_content_module_bak` VALUES ('387', null, '1', null, 'C', '0', '新生儿窒息复苏，应在出生后首先\n', '新生儿疾病', '新生儿窒息,治疗', '1', '1', '2', '1', '1', '1', '0.35', 'A.弹足底或刺激皮肤以引起啼哭\nB.用面罩供氧\nC.用吸管吸出鼻，口腔及咽喉中分粘液和泌物\nD.注射5％碳酸氢钠和呼吸兴奋剂\nE.心脏按摩');
INSERT INTO `g_test_content_module_bak` VALUES ('388', null, '7', null, '因肺表面活性物质由肺Ⅱ型细胞产生，在胎儿20-24周时肺内开始出现，但量\n\n不多，到35周以后迅速上升，如分娩过早则此物质量不足，故本症多发生在早\n\n产儿', '0', '为什么新生儿肺透明膜病多见于早产儿？', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '1', '1', '2', '1', '3', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('389', null, '4', null, '肺表面活性物质   肺表面张力     肺不张', '0', '新生儿患肺透明膜病是由于缺乏__________，使__________增高，致使肺泡\n\n在呼气时逐渐萎陷而呈______________。', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '2', '1', '2', '1', '2', '3', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('390', null, '2', null, 'A   B   C   E', '0', '下列哪些与新生儿肺透明膜病的发病机理有关？\n\n', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '1', '1', '2', '1', '2', '3', '0.00', 'A.肺泡表面活性物质缺乏，表面张力增加\n\nB.气体交换减少，产生缺氧酸中毒使肺血管痉挛\n\nC.肺组织缺氧，毛细血管和肺泡壁渗透性增高，液体外漏\n\nD.肺内淋巴管和静脉运转体液功能不完善，阻碍了换气\n\nE.肺A压力增高，卵圆孔及A导管开放');
INSERT INTO `g_test_content_module_bak` VALUES ('391', null, '1', null, 'B', '0', '新生儿肺透明膜病多见于\n', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '1', '1', '2', '2', '2', '1', '0.00', 'A.急产     B.早产      C.极度寒冷     D.窒息     E.低血糖');
INSERT INTO `g_test_content_module_bak` VALUES ('392', null, '1', null, 'E', '0', '新生儿肺透明膜病病因中最直接的因素是\n\n', '新生儿疾病', '新生儿肺透明膜病,病因和发病机理', '1', '2', '2', '1', '1', '3', '0.08', 'A.母亲患糖尿病        B.新生儿窒息             C.胎盘早剥\n\nD.肺内液体过多        E.肺表面活性物质缺乏');
INSERT INTO `g_test_content_module_bak` VALUES ('393', null, '7', null, '两侧肺野普遍性透明度减低，内有均匀的细小颗粒颗，以后渐融合成片，支气\n\n管则有充气征', '0', '简述新生儿肺透明膜病的X线征', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '1', '1', '2', '3', '2', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('395', null, '1', null, 'B', '0', '新生儿肺透明膜病症状出现的时间多为\n\n', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '2', '1', '2', '3', '1', '1', '0.24', 'A.2小时内                 B.6小时内\n\nC.12小时内                D.24小时内\n\nE.48小时内');
INSERT INTO `g_test_content_module_bak` VALUES ('396', null, '1', null, 'D', '0', '新生儿胎龄35周，生后4小时出现气促，紫绀，进行性加重，两肺呼吸音低，吸气时可听到细湿罗音，最可能的诊断是：\n\n', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '3', '1', '2', '2', '2', '1', '0.09', 'A.新生儿窒息                  B.新生儿羊水吸入综合征\n\nC.新生儿肺炎                  D.新生儿肺透明膜病\n\nE.新生儿颅内出血');
INSERT INTO `g_test_content_module_bak` VALUES ('397', null, '1', null, 'B', '0', '新生儿肺透明膜病未经治疗时常发生：\n\n', '新生儿疾病', '新生儿肺透明膜病,临床表现及辅助检查', '3', '1', '2', '1', '1', '1', '0.07', 'A.呼吸性酸中毒                B.呼吸性酸中毒和代谢性酸中毒\n\nC.呼吸性碱中毒                D.呼吸性碱中毒和代谢性碱中毒\n\nE.代谢性酸中毒');
INSERT INTO `g_test_content_module_bak` VALUES ('398', null, '5', null, '指新生儿出生后较长时间保持肺动脉高压，维持从胎儿型到成人型的过渡性\n血循环俅采弦陨?4小时内出现青紫，呼吸窘迫为主要表现。又称持续胎\n儿循环。', '0', '持续肺动脉高压症', '新生儿疾病', '新生儿肺透明膜病,诊断及鉴别诊断', '2', '1', '2', '1', '2', '1', '0.11', '');
INSERT INTO `g_test_content_module_bak` VALUES ('399', null, '2', null, 'E   A   C   D', '0', '诊断新生儿肺透明膜病何者为重要的依据\n\n', '新生儿疾病', '新生儿肺透明膜病,诊断及鉴别诊断', '3', '1', '2', '3', '1', '3', '0.00', 'A.早产儿，胎龄越小，发病率越高    B.胃液振荡试验阳性\n\nC.有进行性呼吸困难及紫绀          D.X线检查两侧肺透明度减低\n\nE.内有均匀的细小颗粒');
INSERT INTO `g_test_content_module_bak` VALUES ('400', null, '2', null, 'A   C', '0', '湿肺与肺透明膜病有鉴别意义的诊断是\n\n', '新生儿疾病', '新生儿肺透明膜病,诊断及鉴别诊断', '3', '1', '2', '3', '1', '2', '0.00', 'A.多发生在足月儿\n\nB.症状多发生在出生后6小时之内\n\nC.肺部X线表现多样性，如肺泡积液征，间质积液征，肺气肿征等。\n\nD.双肺可闻及湿性罗音\n\nE.胃液振荡试验阴性');
INSERT INTO `g_test_content_module_bak` VALUES ('401', null, '1', null, 'D', '0', '对于早产极低体重儿预防肺透明膜病的有效措施是\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '3', '1', '2', '3', '1', '1', '-0.01', 'A.抗菌素                   B.正压给氧\n\nC.肾上腺皮质激素           D.气管内滴入肺表面活性物质\n\nE.纠正酸中毒');
INSERT INTO `g_test_content_module_bak` VALUES ('402', null, '1', null, 'B', '0', '对早产的孕妇可测羊水中卵磷脂与鞘磷脂的比例，如小于下列比值，新生儿\n\n可能发生肺透明膜病\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '1', '1', '2', '1', '2', '2', '0.00', 'A.1.5:1     B.2:1    C.2.5:1    D.3:1    E.3.5:1');
INSERT INTO `g_test_content_module_bak` VALUES ('403', null, '1', null, 'C', '0', '给一位有发绀和气促的新生儿输100％氧气后，发绀基本消除，此新生儿最可能患\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '1', '1', '2', '1', '1', '1', '0.10', 'A.紫绀型先天性心脏病\nB.新生儿颅内出血\nC.新生儿肺炎\nD.新生儿败血症\nE.以上都不是');
INSERT INTO `g_test_content_module_bak` VALUES ('404', null, '1', null, 'A', '0', '治疗新生儿肺透明膜病的重要措施是\n\n', '新生儿疾病', '新生儿肺透明膜病,预防及治疗', '2', '1', '2', '3', '1', '2', '0.16', 'A.给氧                        B.洋地黄\n\nC.氨苄青霉素                  D.地塞米松\n\nE.洛贝林');
INSERT INTO `g_test_content_module_bak` VALUES ('405', null, '1', null, 'C', '0', '患儿生后2天，皮肤黄染，肝脾肿大，血清总胆红素257umol/L，血型A，Rh阳性.直接抗人球蛋白试验阴性，抗体释放试验IgG抗A抗体阳性，此患儿的诊断是\n', '新生儿疾病', '新生儿黄症,胆红素的正常代谢及黄症临床分', '3', '1', '2', '3', '2', '1', '0.19', 'A.新生儿早期败血症            B.新生儿Rh血型不合溶血病\nC.新生儿AB0血型不合溶血病     D.新生儿生理性黄疸\nE.新生儿红细胞6-磷酸脱氢酶(G-6-PD)缺陷');
INSERT INTO `g_test_content_module_bak` VALUES ('406', null, '1', null, 'C', '0', '新生儿黄疸在生后24小时内出现者应首先考虑\n\n', '新生儿疾病', '新生儿黄症,生理性黄症', '2', '1', '2', '3', '1', '1', '0.12', 'A.先天性胆道闭锁\nB.新生儿生理性黄疸\nC.新生儿溶血症\nD.新生儿败血症\nE.新生儿肝炎');
INSERT INTO `g_test_content_module_bak` VALUES ('407', null, '6', null, '未给合胆红素不溶于水，但能吸收光线，经光照后可发生光照异构作用，导致\n\n分子双键构型转变方向，影响分子内部氢键形成。其极性增强，水溶性增加，\n\n虽未与葡萄糖醛酸结合，仍可随胆汁或尿液排出，从而降低血清胆红素浓度。', '0', '简述光照疗法降黄的作用机理', '新生儿疾病', '新生儿黄症,病理性黄症', '1', '1', '2', '1', '5', '3', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('408', null, '2', null, 'A B C D E', '0', '治疗新生儿黄疸光照疗法的副作用是\n', '新生儿疾病', '新生儿黄症,病理性黄症', '3', '1', '2', '3', '2', '3', '0.35', 'A.发热\nB.皮疹 \nC.核黄素缺乏\nD.低钙血症\nE.腹泻');
INSERT INTO `g_test_content_module_bak` VALUES ('409', null, '2', null, 'B D', '0', '下列哪项符合病理性黄疸的诊断\n', '新生儿疾病', '新生儿黄症,病理性黄症', '3', '1', '2', '3', '2', '3', '0.30', 'A.黄疸于产后48小时出现\nB.血清中胆红素浓度每日上升达90μmol/L\nC.足月儿血清胆红素浓度为200μmol/L\nD.足月儿于生后2周皮肤仍见黄疸\nE.早产儿生后3周皮肤仍见黄疸');
INSERT INTO `g_test_content_module_bak` VALUES ('410', null, '2', null, 'A B C E', '0', 'G6-PD缺乏症可能的诱发原因为\n', '新生儿疾病', '新生儿黄症,病理性黄症', '1', '1', '2', '3', '1', '2', '0.21', 'A.感染         B.接触萘类处理过的衣服       C.服用磺胺\nD.母乳         E.服用痢特灵');
INSERT INTO `g_test_content_module_bak` VALUES ('411', null, '2', null, 'A C E', '0', '下列哪些实验室检查提示患儿为新生儿溶血病\n', '新生儿疾病', '新生儿黄症,病理性黄症', '3', '1', '2', '3', '1', '3', '0.17', 'A.外周白细胞及血红蛋白下降    B.网织红细胞\nC.血清胆红素上升              D.白细胞总数增加\nE.有核红细胞升高');
INSERT INTO `g_test_content_module_bak` VALUES ('412', null, '2', null, 'A C', '0', '患儿生后一周，哺母乳，有发热，体温38.6℃，脐部有少许黄色分泌物，干右肋下1.5cm曾经服阿斯匹林，第二天皮肤出现中等度黄疸，最可能的诊断有：\n', '新生儿疾病', '新生儿黄症,病理性黄症', '1', '1', '2', '3', '2', '2', '0.13', 'A.新生儿败血症     B.新生儿生理性黄疸     C.G-6-P-D缺乏症               D.母乳性黄疸       E.新生儿肝炎');
INSERT INTO `g_test_content_module_bak` VALUES ('413', null, '1', null, 'D', '0', '新生儿肝炎与新生儿胆道闭锁的早期鉴别，下列哪项是主要的：\n\n', '新生儿疾病', '新生儿黄症,病理性黄症', '1', '1', '2', '2', '1', '1', '0.10', 'A.黄疸严重程度\nB.大便颜色\nC.肝脏大小及质地\nD.肝酶升高程度\nE.肝胆B超检查');
INSERT INTO `g_test_content_module_bak` VALUES ('414', null, '1', null, 'B', '0', '新生儿溶血症发生黄疸时可采用的酶诱导剂是\n\n', '新生儿疾病', '新生儿黄症,病理性黄症', '3', '1', '2', '1', '1', '1', '0.12', 'A.安定，洛贝林               B.苯巴比妥，尼可刹米(可拉明)\n\nC.阿米妥，咖啡因             D.辅酶A，ATP\n\nE.细胞色素C');
INSERT INTO `g_test_content_module_bak` VALUES ('415', null, '1', null, 'C', '0', '一位ABO溶血患儿，需进行换血治疗，患儿为B型血，血源选择应是\n\n', '新生儿疾病', '新生儿黄症,病理性黄症', '3', '1', '2', '3', '1', '1', '0.12', 'A.O型血球，B型血浆             B.B型血球，B型血浆\n\nC.0型血球，AB型血浆            D.B型血球，O型血浆\n\nE.AB型血球，O型血浆');
INSERT INTO `g_test_content_module_bak` VALUES ('416', null, '1', null, 'D', '0', '下列哪一点不是新生儿病理性黄疸的临床特点\n\n', '新生儿疾病', '新生儿黄症,病理性黄症', '2', '1', '2', '3', '1', '1', '0.37', 'A.生后24小时内可见黄疸\n\nB.足月儿血清胆红素>204μmol/L(12mg/dl)\n\nC.早产儿血清胆红素>259μmol/L(15mg/dl)\n\nD.血清胆红素每天上升26μmol/L(1.5mg/dl)\n\nE.足月儿黄疸14天以上不退');
INSERT INTO `g_test_content_module_bak` VALUES ('417', null, '5', null, '新生儿溶血症是指母婴血型((ABO系统或Rh系统)不合，母亲对胎儿红细胞发\n生同种免疫反应所引起的溶血病', '0', '新生儿溶血症', '新生儿疾病', '新生儿溶血症,发病机理', '2', '1', '2', '3', '2', '1', '0.00', '');
