INSERT INTO `g_test_content_module_bak` VALUES ('548', null, '1', null, 'B', '0', 'Ⅱ，Ⅲ度营养不良在调整饮食时，开始热量应为：\n\n', '营养性疾病', '营养不良,治疗', '2', '1', '2', '3', '1', '2', '0.25', 'A.20～30Cal/kg/日      B.40～60Cal/kg/日       C.70～90Cal/kg/日\n\nD.100～120Cal/kg/日    E.120～140Cal/kg/日');
