INSERT INTO `g_test_content_module_bak` VALUES ('593', null, '7', null, '', '0', '简述佝偻病患儿不同年龄骨骼畸形变化的特点。\n\n颅骨软化：多见于3-6个月婴儿\n\n方颅：多见于5-9个月以上婴儿\n\n胸部畸形，如鸡胸，漏斗胸赫氏沟多见于1岁左右患儿\n\n四肢畸形，手脚多见于6个月以后患儿\n\n         O.X型腿多见于1岁以后行走站立小儿', '营养性疾病', '维生素D缺乏性佝偻病,临床表现', '2', '1', '2', '3', '1', '3', '0.00', '');
