INSERT INTO `g_test_content_module_bak` VALUES ('633', null, '2', null, 'A B C E', '0', '预防VD缺乏性佝偻病有哪些措施？\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '2', '1', '2', '3', '1', '2', '0.00', 'A.早产儿自生后两周起补VitD\nB.2岁以后生长发育减慢，一般不要继续补充VitD\nC.青春前期可考虑加服VD预防量\nD.VD突击治疗后，给予预防量维持3个月\nE.母乳期补充VitD');
INSERT INTO `g_test_content_module_bak` VALUES ('634', null, '2', null, 'A   B   C   E', '0', '佝偻病的预防措施\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '1', '1', '2', '3', '1', '2', '0.00', 'A.孕妇要注意饮食及晒太阳             \n\nB.婴幼儿维生素D预防量400u/日\n\nC.早产儿维生素D预防量前三个月800u/日 \n\nD.维生素D预防量应用在学龄前\n\nE.口服吸收不良者可肌注VitD');
INSERT INTO `g_test_content_module_bak` VALUES ('635', null, '1', null, 'B', '0', '佝偻病一般性预防用维生素D制量是\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '2', '1', '2', '3', '1', '1', '0.00', 'A.200单位/日           B.400单位/日          C.1000单位/日\n\nD.800单位/日           E.2000单位/日');
INSERT INTO `g_test_content_module_bak` VALUES ('636', null, '1', null, 'D', '0', '预防佝偻病应强调：\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '1', '1', '2', '3', '1', '1', '0.12', 'A.合理喂养        B.经常口服鱼肝油            C.经常口服钙片\n\nD.经常晒太阳      E.母亲孕期与哺乳期的保健');
INSERT INTO `g_test_content_module_bak` VALUES ('637', null, '1', null, 'A', '0', '维生素D治疗佝偻病的剂量应为\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '1', '1', '2', '3', '2', '1', '0.10', 'A.依病期不同而剂量不同             B.不同病期剂量相同\nC.先用小剂量，逐渐加大剂量         D.剂量越大越好\nE.剂量越少越安全');
INSERT INTO `g_test_content_module_bak` VALUES ('638', null, '1', null, 'B', '0', '预防维生素D缺乏性佝偻病，哪项不正确：\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '2', '1', '2', '3', '2', '2', '0.45', 'A.早产儿出生后二周服VitD \nB.VitD的预防应自出生开始直到学龄前期\nC.青春前期应服用预防量VitD\nD.新生儿自1月后就应服VitD400u/日\nE.孕母乳母及婴儿均应多晒太阳');
INSERT INTO `g_test_content_module_bak` VALUES ('639', null, '1', null, 'D', '0', '佝偻病活动期突击疗法是\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '2', '1', '2', '3', '1', '1', '0.00', 'A.维生素D3 10万u/次/月共3次      B.维生素D3 20万u/次/月共2次\n\nC.维生素D2 20万u只1次            D.维生素D3 30万u次/月共1～2次\n\nE.维生素D3 100万u只1次');
INSERT INTO `g_test_content_module_bak` VALUES ('640', null, '1', null, 'C', '0', '佝偻病活动早期突击疗法是\n\n', '营养性疾病', '维生素D缺乏性佝偻病,治疗及预防', '2', '1', '2', '3', '1', '1', '0.19', 'A.维生素D3 10万u次/月共3次       \n\nB.维生素D3 20万u次/月共2次\n\nC.维生素D3 30万u只一次\n\nD.维生素D2 60万u1-2周1次，连注2-3次\n\nE.维生素D3 100万u只1次');
INSERT INTO `g_test_content_module_bak` VALUES ('641', null, '1', null, 'D', '0', '低钙血症时下列哪一项是错误的？\n', '营养性疾病', '维生素D缺乏性手足搐搦症,病因和发病机理', '1', '1', '2', '3', '1', '2', '0.16', 'A.刺激甲状旁腺素分泌增加        B.间接刺激1，25(OH)2D3合成\nC.抑制降钙素分泌                D.心电图示S-T段缩短\nE.婴儿易发生癫痫样大发作');
INSERT INTO `g_test_content_module_bak` VALUES ('642', null, '1', null, 'C', '0', '婴儿手足搐搦症的发病机理与佝偻病的不同点在于：\n\n', '营养性疾病', '维生素D缺乏性手足搐搦症,病因和发病机理', '1', '1', '2', '3', '1', '1', '0.27', 'A.钙吸收代谢障碍                   B.磷吸收代谢障碍\n\nC.甲状旁腺调节功能不足             D.维生素D缺乏\n\nE.神经系统兴奋性增高');
