INSERT INTO `g_test_content_module_bak` VALUES ('664', null, '1', null, 'D', '0', '5个月婴儿，发作性吸气性喉鸣，哭闹时出现吸气时呼吸困难伴紫钳，间歇期患儿面色精神正常，呼吸平顺，查体体温正常，心肺未间及异常，最可能的诊断是\n', '营养性疾病', '维生素D缺乏性手足搐搦症,诊断与鉴别诊断', '3', '1', '2', '3', '1', '3', '0.20', 'A.急性喉炎              B.气管异物          C.喘息性支气管炎\nD.维生素D缺乏性喉痉挛   E.毛细支气管炎');
