INSERT INTO `g_test_content_module_bak` VALUES ('708', null, '1', null, 'E', '0', '下列哪一种不是急性上呼吸道感染的常见病原体\n\n', '呼吸系疾病', '急性上呼吸道感染,临床表现', '1', '1', '2', '3', '1', '1', '0.16', 'A.鼻病毒                      B.合胞病毒\n\nC.副流感病毒                  D.流冒病毒\n\nE.轮状病毒');
INSERT INTO `g_test_content_module_bak` VALUES ('709', null, '1', null, 'B', '0', '急性支气管炎的病因下列哪一项不正确\n', '呼吸系疾病', '急性上呼吸道感染,临床表现', '1', '2', '2', '3', '1', '1', '0.24', 'A.凡能引起上呼吸道感染的病原体皆可引起支气管炎\nB.常在细菌感染的基础上继发病毒感染\nC.特异性素质常为反复发作的原因\nD.空气污染亦为本病的发病因素\nE.营养不良，副鼻窦炎患儿易反复发作');
INSERT INTO `g_test_content_module_bak` VALUES ('710', null, '1', null, 'C', '0', '下列哪一项不是哮喘性支气管炎的特点\n\n', '呼吸系疾病', '急性上呼吸道感染,临床表现', '3', '2', '2', '3', '1', '2', '0.53', 'A.多见于3岁以下，常有湿疹病史\n\nB.有类似哮喘症状\n\nC.听诊两肺满布哮鸣音及中细湿罗音\n\nD.可有发热，白细胞增高等感染表现\n\nE.有反复发作倾向');
INSERT INTO `g_test_content_module_bak` VALUES ('711', null, '2', null, 'B   D   E', '0', '急性感染性喉炎\n\n', '呼吸系疾病', '急性上呼吸道感染,临床表现', '2', '2', '2', '2', '1', '2', '0.00', 'A.常见于年长儿                B.病原体常为病毒或细菌\n\nC.以夏秋季发病较多            D.新生儿期极少发病\n\nE.常见于婴幼儿');
