INSERT INTO `g_test_content_module_bak` VALUES ('723', null, '1', null, 'C', '0', '患儿6岁，咳嗽发作半天，不发热，既往有类似发作史2年，春秋季好发，有轻至中度呼吸困难，体征：两肺满布哮鸣音，诊断应考虑\n', '呼吸系疾病', '支气管炎,临床表现', '3', '1', '2', '3', '1', '1', '0.01', 'A.支气管炎                    B.支气管肺炎\nC.支气管哮喘                  D.哮喘性支气管炎\nE.毛细支气管炎');
INSERT INTO `g_test_content_module_bak` VALUES ('724', null, '1', null, 'B', '0', '一般下列何种疾病在X线胸片可见到梗阻性肺气肿症象 \n\n', '呼吸系疾病', '支气管炎,临床表现', '1', '1', '2', '3', '1', '1', '0.11', 'A.支气管炎                 B.毛细支气管炎\n\nC.支气管肺炎               D.支原体肺炎 \n\nE.大叶性肺炎');
INSERT INTO `g_test_content_module_bak` VALUES ('725', null, '1', null, 'B', '0', '6个月婴儿咳嗽，喘憋2天，烦躁， 发绀，体温38℃呼吸64次/分，三凹征，双肺闻哮鸣及细湿罗音肝肋下2cm，白细胞10.0×109/L X线示双肺透光度增加双下肺纹增粗，诊断应考虑\n', '呼吸系疾病', '支气管炎,临床表现', '1', '1', '2', '3', '1', '2', '0.26', 'A.哮喘性支气管炎              B.毛细支气管炎\nC.支气管哮喘                  D.肺结核\nE.婴儿急性上呼吸道感染');
INSERT INTO `g_test_content_module_bak` VALUES ('726', null, '1', null, 'B', '0', '毛细支气管炎常见病原体\n\n', '呼吸系疾病', '支气管炎,临床表现', '2', '1', '2', '1', '1', '1', '0.22', 'A.腺病毒                      B.呼吸道合胞病毒\n\nC.柯萨奇病毒                  D.巨细胞病毒\n\nE.鼻病毒');
