INSERT INTO `g_test_content_module_bak` VALUES ('728', null, '1', null, 'B', '0', '支气管炎患儿尽量少用非那根是因为\n', '呼吸系疾病', '支气管炎,治疗', '1', '1', '2', '3', '2', '1', '0.11', 'A.抑制呼吸                  B.可使痰液干燥，影响湿化排出\nC.抑制咳嗽反射              D.加重喉水肿\nE.易致过敏反应');
