INSERT INTO `g_test_content_module_bak` VALUES ('770', null, '6', null, '1.呼吸困难突然加重，烦燥不安，面色苍白或发绀 2.心率增快，婴儿&gt;160/min，新生儿&gt;180/min 3.心音低钝或心脏扩大，心律紊乱 4.肝脏短期内上下经增大1.5cm以上 5 呼吸困难和肺部罗音短期内加重，增多', '0', '试述小儿肺炎合并心力衰竭的诊断要点', '呼吸系疾病', '支气管肺炎,诊断与鉴别诊断', '3', '2', '2', '3', '10', '1', '0.03', '');
