INSERT INTO `g_test_content_module_bak` VALUES ('911', null, '1', null, 'E', '0', '急生坏死性肠炎的主要病因是\n\n', '消化道疾病', '急性坏死性肠炎,病因与病理', '2', '2', '2', '1', '1', '2', '0.00', 'A.细菌感染                    B.病毒感染\n\nC.蛔虫感染                    D.暴饮暴食，吃生冷不洁食物\n\nE.机体对致病因子敏感性增高');
