INSERT INTO `g_test_content_module_bak` VALUES ('913', null, '1', null, 'A', '0', '急性坏死性肠炎的病变主要部位\n\n', '消化道疾病', '急性坏死性肠炎,病因与病理', '2', '2', '2', '1', '1', '1', '0.09', 'A.空肠和回肠             B.直肠和乙状结肠\n\nC.全部小肠               D.横结肠和降结肠\n\nE.食管与小肠');
