INSERT INTO `g_test_content_module_bak` VALUES ('948', null, '1', null, 'C', '0', '正常10月婴儿，其心率应是\n\n', '循环系统疾病', '小儿循环系统特点,生理特点', '2', '1', '2', '1', '1', '2', '0.13', 'A.>140次/分                   B.120-140次/分\n\nC.110-130次/分                D.100-120次/分\n\nE.80-100次/分');
INSERT INTO `g_test_content_module_bak` VALUES ('949', null, '5', null, '由于心脏排血能力不能满足人体需要，引起静脉系统淤血及一系列代偿机制，由此产生一系列临床症状和体征。', '0', '心力衰竭', '循环系统疾病', '小儿循环系统特点,生理特点', '1', '1', '2', '1', '3', '1', '0.10', '');
INSERT INTO `g_test_content_module_bak` VALUES ('950', null, '1', null, 'E', '0', '小儿心率随年龄增长而逐渐减慢 ，下列哪一项不正确\n', '循环系统疾病', '小儿循环系统特点,生理特点', '2', '2', '2', '1', '1', '1', '0.36', 'A.新生儿平均120～140次/min      B.&lt;1岁为110～130次/min\nC.～3岁为100～120次/min         D.～7岁为80～100次/min\nE.～14岁60～70次/min');
INSERT INTO `g_test_content_module_bak` VALUES ('951', null, '2', null, 'A   B   C', '0', '小儿动脉血压的论述下列哪些是正确的？\n\n', '循环系统疾病', '小儿循环系统特点,生理特点', '2', '2', '2', '1', '1', '1', '0.00', 'A.收缩期血压=[(年龄×2)+80]×0.133kpa\n\nB.收缩压高于上述标准KPa数的2.67kpa(20mmHg)为高血压\n\nC.收缩期血压的2/3为舒张期血压\n\nD.低于收缩期血压的3.325kpa为低血压\n\nE.上肢的血压略高于下肢');
INSERT INTO `g_test_content_module_bak` VALUES ('952', null, '4', null, '柯萨奇B    亲心肌性    流行性', '0', '__________病毒是引起病毒性心肌炎发病率最高的病原体，这是由于它的高\n\n度的__________和____________的特性', '循环系统疾病', '病毒性心肌炎,病因、发病机理和病理', '2', '1', '2', '1', '1', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('953', null, '1', null, 'C', '0', '小儿病毒心肌炎致病率最高的病毒是\n', '循环系统疾病', '病毒性心肌炎,病因、发病机理和病理', '2', '1', '2', '1', '1', '1', '0.20', 'A.流感病毒                    B.副流感病毒\nC.柯萨奇B与A型病毒            D.埃可病毒\nE.腺病毒');
INSERT INTO `g_test_content_module_bak` VALUES ('954', null, '7', null, '1.病毒直接侵犯心肌，在心肌细胞内复制引起心肌细胞坏死间质炎症\n\n2.自身免疫反应。在病毒感染后2周左右发病由于机体继发性Ⅲ型免疫反应\n\n而使心肌受累', '0', '简述病毒性心肌炎发病机理', '循环系统疾病', '病毒性心肌炎,病因、发病机理和病理', '1', '1', '2', '1', '3', '1', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('955', null, '5', null, '心源性休克是由于急性心排血功能障碍引起组织和器官灌注不足，以迅速发\n展的休克。', '0', '心源性休克', '循环系统疾病', '病毒性心肌炎,临床表现', '1', '1', '2', '1', '3', '1', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('956', null, '1', null, 'D', '0', '小儿病毒性心肌炎最常见的后遗症是\n\n', '循环系统疾病', '病毒性心肌炎,临床表现', '2', '1', '2', '1', '1', '1', '0.26', 'A.心电图显示ST-T段改变        B.Ⅰ°-Ⅱ°房室传导阻滞\n\nC.束支传导阻滞                D.过早搏动\n\nE.心脏扩大');
INSERT INTO `g_test_content_module_bak` VALUES ('957', null, '1', null, 'C', '0', '提示早期或无症状性心肌炎的重要症状是\n\n', '循环系统疾病', '病毒性心肌炎,临床表现', '1', '1', '2', '1', '1', '1', '0.15', 'A.诉乏力，头晕              B.腹痛，腹泻，恶心，呕吐等消化道症状\n\nC.肌肉痛                    D.呼吸困难\n\nE.紫绀');
INSERT INTO `g_test_content_module_bak` VALUES ('958', null, '1', null, 'E', '0', '以下哪项不支持轻型病毒性心肌炎临床表现？\n\n', '循环系统疾病', '病毒性心肌炎,临床表现', '1', '1', '2', '1', '1', '1', '0.18', 'A.胸闷，心悸                  B.心脏轻度扩大\n\nC.心动过速                    D.心音低钝及奔马律\n\nE.严重心律紊乱');
INSERT INTO `g_test_content_module_bak` VALUES ('959', null, '1', null, 'E', '0', '乳酸脱氢酶同功酶出现下列何种情况，对病毒性心肌炎的诊断有价值\n', '循环系统疾病', '病毒性心肌炎,辅助检查', '1', '1', '2', '3', '2', '1', '0.37', 'A.LDH2&gt;LDH1&gt;LDH3&gt;LDH4&gt;LDH5    B.LDH3&gt;LDH1&gt;LDH2&gt;LDH4&gt;LDH5\nC.LDH4&gt;LDH1&gt;LDH2&gt;LDH3&gt;LDH5    D.LDH5&gt;LDH1&gt;LDH2&gt;LDH3&gt;LDH4\nE.LDH1&gt;LDH2&gt;LDH3&gt;LDH4&gt;LDH5');
INSERT INTO `g_test_content_module_bak` VALUES ('960', null, '2', null, 'A C E', '0', '小儿病毒性心肌炎LDH(乳酸脱氢酶)活性测定具有怎样的特征\n', '循环系统疾病', '病毒性心肌炎,辅助检查', '3', '1', '2', '3', '1', '3', '0.25', 'A.病后1-2天活性上升               B.高峰时间在病后2周\nC.持续时间可达2月                 D.特异性强，敏感度高\nE.LDH同功酶活性测定意义比LDH大');
INSERT INTO `g_test_content_module_bak` VALUES ('961', null, '4', null, '急慢性心功能不全或心脑综合征\n\n有奔马律或心包炎心脏扩大\n\n发病1个月CPK-MB↑\n\n心电图明显异常', '0', '全国9省市协作制订的小儿病毒性心肌炎临床主要诊断依据是__________、\n\n___________、____________、_______________。', '循环系统疾病', '病毒性心肌炎,诊断与鉴别诊断', '2', '1', '2', '1', '3', '3', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('962', null, '6', null, 'A 三尖瓣脱垂起病早，有持续性青紫，生长发育障碍\n\nB 心电图示P波示尖宽大\n\nC X线见三尖瓣脱垂主要以右房扩大为主。肺动脉段凹隔，肺血减少，而慢性\n\n心肌炎以左右室为主，肺动脉段突出，肺充血瘀血。', '0', '先天性三尖瓣脱垂易与慢性心肌炎混淆，请试述其鉴别要点', '循环系统疾病', '病毒性心肌炎,诊断与鉴别诊断', '3', '1', '2', '1', '7', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('963', null, '7', null, '一。诊断：心肌炎，病毒性可能\n二。依据：主要依据 X线示心影扩大\n        次要依据 有发热，腹泻病毒感染史，有无力，气促二个以上症状心\n                 前区第一心音低钝E&', '0', '病例摘要\n男，10岁，浙江淀海籍，低热2周，气促伴乏力一月入院，病初淋雨后发热38℃\n伴腹泻水样便3-4次/日，经治疗后热退，腹泻止但患儿显乏力，活动后气促，\n咳嗽，轻度气喘，抗炎治疗无效过去史和家族史无特殊。体检：T38℃双肺正常\n心界向左下稍大，HR94次/分，心前区第1心音低钝，5-8次/分早搏，无杂音，肝\n肋下1cm尚软，下肢不肿，关节无红肿，无杵状指((趾).X线：示心影响左下扩大\nE.K.G窦性心律不齐，频发房性早搏。 心肌酶谱示 CPK113H/L   LDH175U/L\nWB', '循环系统疾病', '病毒性心肌炎,诊断与鉴别诊断', '3', '1', '2', '1', '15', '3', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('964', null, '1', null, 'C', '0', '病毒性心肌炎表酶学检查下列那种特异性最高\n\n', '循环系统疾病', '病毒性心肌炎,诊断与鉴别诊断', '3', '1', '2', '1', '1', '1', '0.11', 'A.GOT                         B.GPT\n\nC.LDH和CPK同功酶              D.AKP\n\nE.LDH');
INSERT INTO `g_test_content_module_bak` VALUES ('965', null, '2', null, 'A B C D E', '0', '病毒性心肌炎并发心衰应用尖洋地黄制剂应注意：\n', '循环系统疾病', '病毒性心肌炎,治疗', '1', '1', '2', '1', '2', '3', '0.19', 'A.选用作用快，排泄快制剂\nB.饱和量较常用量减少\nC.洋地黄化时间延长\nD.心衰控制后，维持量继用1个月以上\nE.与利尿合用，可减少洋地黄用量');
INSERT INTO `g_test_content_module_bak` VALUES ('966', null, '2', null, 'D', '0', '病毒性心肌炎发生心力衰竭时，洋地黄用量应偏小这主要是由于\n\n', '循环系统疾病', '病毒性心肌炎,治疗', '1', '1', '2', '1', '2', '3', '0.11', 'A.心肌炎对洋地黄吸收加快    B.心肌炎对洋地黄排泄减慢\n\nC.心肌炎洋地黄作用不敏感    D.心肌炎对洋地黄作用较敏感\n\nE.心肌炎易发生水电紊乱');
INSERT INTO `g_test_content_module_bak` VALUES ('967', null, '1', null, 'A', '0', '病毒性心肌炎应用肾上腺皮质激素的适应症\n', '循环系统疾病', '病毒性心肌炎,治疗', '3', '1', '2', '1', '2', '3', '0.35', 'A.全身中毒症状严重，有心衰或心源性休克\nB.严重的心律紊乱\nC.病程长，反复发作，一般治疗效不佳 \nD.病初早期，GOT，CPK酶升高时\nE.血中查到病毒原体者');
