INSERT INTO `g_test_content_module_bak` VALUES ('1124', null, '2', null, 'A   B   C', '0', '下列哪些是活动性肺结核的有力诊断依据？\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '2', '2', '2', '3', '1', '1', '0.00', 'A.痰或胃液中找到结核菌        B.血沉加快\n\nC.肺部X线检查阳性病灶         D.白细胞计数增高\n\nE.有贫血存在');
INSERT INTO `g_test_content_module_bak` VALUES ('1125', null, '2', null, 'B D', '0', '卡介苗接种后结核菌素反应的特点\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '1', '1', '2', '2', '1', '3', '0.00', 'A.硬结直径&gt;15mm                 B.每次反应时间短3-4天\nC.硬结质软，色深，边缘清晰      D.持续时间短3-4年转阴\nE.年龄越小,反应越大');
INSERT INTO `g_test_content_module_bak` VALUES ('1126', null, '2', null, 'A   C', '0', '自然感染后结核菌素反应的特点包括？\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '1', '1', '2', '2', '1', '2', '0.00', 'A.硬结直径>10-15mm               B.反应持续7-10天\n\nC.硬结反应，质硬，色深，边缘清   D.反应时间短10-20天\n\nE.年龄越小,反应越大');
INSERT INTO `g_test_content_module_bak` VALUES ('1127', null, '2', null, 'A B C E', '0', '结核菌素试验在下列哪些情况可出现假阴性。\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '1', '1', '2', '2', '1', '2', '0.00', 'A.急性传染病                  B.严重结核病\nC.应用免疫抑制剂              D.合并皮肤病时\nE.结核感染3周以内');
INSERT INTO `g_test_content_module_bak` VALUES ('1128', null, '2', null, 'D', '0', '从体液找结核杆菌确诊结核病，对于幼儿下列哪些项目不正确\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '3', '1', '2', '3', '1', '3', '0.00', 'A.痰液收集困难\n\nB.30～40％病例胃吸出物质中可获细菌\n\nC.采用原片法或荧光染色法查结核杆菌\n\nD.应在中午进行抽吸由呼吸道分泌入胃内的液体作标本。\n\nE.PCR检测用于脑脊液检查');
