INSERT INTO `g_test_content_module_bak` VALUES ('1134', null, '6', null, '结核菌的耐药性是指细菌对抗结核药物的抵抗力鸵┬钥煞治街掷嘈停?\n一种是原发耐药，指从未用过抗结核药物治疗的病人，病灶中的结核菌对药\n\n物有了耐药性，这种耐药性的产生。通常是由于病人感染了耐药菌的缘故?\n一种是继发耐药，指感染了敏感菌的肺结核病人，因为治疗不当造成结核菌\n\n产生耐药性。', '0', '什么是结核菌的耐药性？', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '1', '2', '2', '1', '5', '3', '0.00', '');
